/*common css*/

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

a {
    text-decoration: none;
    color: #333;
}

ul {
    list-style-type: none;
}

body {
    background-color: #eee;
    color: #333;
}

img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.container {
    width: 1200px;
    margin: 0 auto;
}


/* header left css */

.globalHeader {
    display: flex;
    justify-content: space-between;
    border: 1px solid #ddd;
}

.globalHeader>.logo {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-left: 20px;
}

.globalHeader>.logo>.logo_img {
    width: 36px;
}

.globalHeader>.logo>.logo_name {
    margin-left: 20px;
    vertical-align: middle;
}


/* header right css */

.nav {
    display: flex;
    align-items: center;
    margin-right: 60px;
}

.nav>div {
    margin-left: 20px;
}


/* main css */

.globalMain {
    border: 1px solid #ddd;
    display: flex;
    flex-wrap: wrap;
    margin-top: 8px;
}

.globalMain>.works_items {
    width: 350px;
    height: 250px;
    background-color: #1F2229;
    border-radius: 20px;
    display: flex;
    justify-content: center;
    margin-top: 40px;
    cursor: pointer;
    margin-right: 49px;
}

.works_items>.works_items_img {
    width: 300px;
    height: 160px;
    border-radius: 10px;
    margin-top: 20px;
}

.works_items_img>img {
    border-radius: 10px;
}

.works_items_img>img:hover {
    -webkit-animation: shake 0.25s;
}

@-webkit-keyframes shake {
    0%,
    10%,
    55%,
    90%,
    94%,
    98%,
    100% {
        -webkit-transform: scale(1, 1);
    }
    30% {
        -webkit-transform: scale(1.14, 0.86);
    }
    75% {
        -webkit-transform: scale(0.92, 1.08);
    }
    92% {
        -webkit-transform: scale(1.04, 0.96);
    }
    96% {
        -webkit-transform: scale(1.02, 0.98);
    }
    99% {
        -webkit-transform: scale(1.01, 0.99);
    }
}
